import { Component,OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';
import { FormGroup, FormControl, Validators,FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  // email :string="";
  // password :string="";
  loginForm:FormGroup |any

  constructor(public authService:AuthService, private router: Router,private formBuilder: FormBuilder) { }

  ngOnInit() {
    this.loginForm = this.formBuilder.group({
      //controlname: ['initial value', rules]

      email:['',[Validators.required,Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$")]],
      password: ['', [Validators.required]]

    });

  }

  login(){
    // let auth = {
    //   email:this.email,
    //   password: this.password
    // }
    this.authService.signIn(this.loginForm.value).subscribe((res:any)=>{
      if (!res.status) alert(res.message)
      else{
        this.authService.id = res.id;
        this.authService.name=res.name;
        this.router.navigate(['/chat'])
       //alert(res.message)
      }
    });
  }

}
<div class="container container-fluid col-xs-6 col-xs-offset-3">

<h2 class="text-center page-header">Welcome to FriendsChat</h2>
<form [formGroup]="loginForm" (ngSubmit)="login()">
  <br>
    <div class="form-group">
      <label for="inputEmail" class="sr-only">Email address</label>
      <input [class.is-invalid] = "loginForm.get('email').invalid && loginForm.get('email').touched" id="inputEmail" formControlName="email" name="email" class="form-control" placeholder="Email address" required autofocus="" type="email">
      <small *ngIf="loginForm.get('email').errors?.pattern" class="text-danger">Please provide a valid email address ></small>
    </div>
  <br>
    <div class="form-group">
      <label for="inputPassword" class="sr-only">Password</label>
      <input id="inputPassword" formControlName="password" name="password" class="form-control" placeholder="Password" required="" type="password">
    </div>
  <br>

    <div class="form-group">
      <button [disabled]="loginForm.invalid" class="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
    </div>

    <div class="text-right">
        Don't have an account?<a [routerLink]="['/register']">Register</a>
    </div>
  </form>
  </div>
