import { Component ,OnInit} from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {


  name :String="";
  email :String ="";
  password :String="";
  confirmPassword :String="";

  constructor(public authService:AuthService, private router: Router) {  }

  ngOnInit() {
  }

  register(){
    if(this.password != this.confirmPassword)
    {
      alert("Password does not match !")
    }
    else{
    let auth = {
      name:this.name,
      email:this.email,
      password: this.password
    }
    this.authService.signUp(auth).subscribe((res:any)=>{
      if (!res.status) alert("invalid E-mail address")
      else{
        this.router.navigate(['/login'])
      }
    })
  }
}

}

<div class="container container-fluid col-xs-6 col-xs-offset-3">

    <h2 class="text-center page-header">Create account</h2>
    <form ng-app="myApp" ng-controller="validateCtrl" 
    name="myForm" novalidate (ngSubmit)="register()">
  <br>
    <div class="form-group">
      <label for="inputName" class="sr-only">Name</label>
      <input id="inputName" [(ngModel)]="name" class="form-control" placeholder="Name"  autofocus="true" type="text" name="name" required>
      <span style="color:red" ng-show="myForm.user.$dirty && myForm.user.$invalid">
        <span ng-show="myForm.user.$error.required">Username is required.</span>
        </span>

    </div>
  <br>
    <div class="form-group">
      <label for="inputEmail" class="sr-only">Email address</label>
      <input id="inputEmail" [(ngModel)]="email" class="form-control" name="email" placeholder="Email address"  autofocus="" type="email" required>
    </div>
  <br>
    <div class="form-group">
      <label for="inputPassword" class="sr-only">Password</label>
      <input id="inputPassword" [(ngModel)]="password" name="password" class="form-control" placeholder="Password" required type="password">
    </div>
  <br>
  <div class="form-group">
    <label for="confirmPassword" class="sr-only">Password</label>
    <input id="confirmPassword" [(ngModel)]="confirmPassword" name="confirmPpassword" class="form-control" placeholder="Confirm Password" required type="password">
  </div>
<br>
    <div class="form-group">
      <button class="btn btn-lg btn-primary btn-block"  type="submit">Sign up</button>
      &nbsp;&nbsp;&nbsp;<a [routerLink]="['/login']">Back to Login</a>
    </div>
  </form>
  <br>
  </div>
  <script>
    var app = angular.module('myApp', []);
    app.controller('validateCtrl', function($scope) {
        $scope.user = 'John Doe';
        $scope.email = 'john.doe@gmail.com';
    });
    </script>
