import { Component ,OnInit} from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';
import { FormGroup, FormControl, Validators,FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  registerForm:FormGroup;

  // name :String="";
  // email :String ="";
  // password :String="";
  // confirmPassword :String="";

  constructor(public authService:AuthService, private router: Router,private formBuilder: FormBuilder,) {  }

  ngOnInit() {
    this.registerForm = this.formBuilder.group({
      //controlname: ['initial value', rules]
      name: [
        '',
        [
          Validators.required
  ],
      ],
      email:['',[Validators.required,Validators.pattern("^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$")]],
      password: ['', [Validators.required, Validators.minLength(4)]],
      confirmPass: ['', [Validators.required, Validators.minLength(4)]],
    });

  }

  register(){
    // if(this.name==''|| this.email==''|| this.password==''|| this.confirmPassword=='')
    // alert("All fields are Mandatory")
    if(this.password != this.confirmPassword)
    {
      alert("Password does not match !")
    }
    else{
    // let auth = {
    //   name:this.name,
    //   email:this.email,
    //   password: this.password
    // }
    this.authService.signUp(this.registerForm.value).subscribe((res:any)=>{
      if (!res.status) alert("invalid E-mail address")
      else{
        this.router.navigate(['/login'])
      }
    })
  }
}

}

<div class="container container-fluid col-xs-6 col-xs-offset-3">

    <h2 class="text-center page-header">Create account</h2>
    <form [formGroup]="registerForm" (ngSubmit)="register()">
  <br>
    <div class="form-group">
      <label for="inputName" class="sr-only">Name</label>
      <input id="inputName" class="form-control" placeholder="Name"  autofocus="true" type="text" name="name" required>


    </div>
  <br>
    <div class="form-group">
      <label for="inputEmail" class="sr-only">Email address</label>
      <input id="inputEmail" class="form-control" name="email" placeholder="Email address"  autofocus="" type="email" required >
    </div>
  <br>
    <div class="form-group">
      <label for="inputPassword" class="sr-only">Password</label>
      <input id="inputPassword"  name="password" class="form-control" placeholder="Password" required type="password">
    </div>
  <br>
  <div class="form-group">
    <label for="confirmPassword" class="sr-only">Password</label>
    <input id="confirmPassword" name="confirmPpassword" class="form-control" placeholder="Confirm Password" required type="password">
  </div>
<br>
    <div class="form-group">
      <button class="btn btn-lg btn-primary btn-block"  type="submit">Sign up</button>
      &nbsp;&nbsp;&nbsp;<a [routerLink]="['/login']">Back to Login</a>
    </div>
  </form>
  <br>
  </div>
