import { Component,OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  email :string="";
  password :string="";

  constructor(public authService:AuthService, private router: Router) { }

  ngOnInit() {
  }

  login(){
    let auth = {
      email:this.email,
      password: this.password
    }
    this.authService.signIn(auth).subscribe((res:any)=>{
      if (typeof(res.status)=="undefined") alert("Try Login again")
      else{
        this.authService.id = res.id;
        this.authService.name=res.name;
        this.router.navigate(['/chat'])
      }
    });
  }

}
<div class="container-fluid col-xs-6 col-xs-offset-3">


    <h2 class="text-center page-header">Welcome to our Chat</h2>

    <div class="form-group">
      <label for="inputEmail" class="sr-only">Email address</label>
      <input id="inputEmail" [(ngModel)]="email" class="form-control" placeholder="Email address" required="" autofocus="" type="email">
    </div>

    <div class="form-group">
      <label for="inputPassword" class="sr-only">Password</label>
      <input id="inputPassword" [(ngModel)]="password" class="form-control" placeholder="Password" required="" type="password">
    </div>

    <div class="form-group">
      <button class="btn btn-lg btn-primary btn-block" (click)="login()" type="submit">Sign in</button>
    </div>

    <div class="text-right">
        <a [routerLink]="['/login']">Already have an account?</a>
    </div>

  </div>
